section.para:
Example beginning of a TextMarker file: PACKAGE de.uniwue.example;
// import the types of this type system // (located in the descriptor folder -> de.uniwue.example folder) TYPESYSTEM types.BibtexTypeSystem;
SCRIPT de.uniwue.example.Author; SCRIPT de.uniwue.example.Title; SCRIPT de.uniwue.example.Year;
Syntax of declarations: Declaration -> "DECLARE" (AnnotationType)? Identifier ("," Identifier )* | "DECLARE" AnnotationType Identifier ( "(" FeatureDeclaration ")" )? FeatureDeclaration -> ( (AnnotationType | "STRING" | "INT" | "FLOAT" "DOUBLE" | "BOOLEAN") Identifier) )+ VariableDeclaration -> (("TYPE" Identifier ("," Identifier)* ("=" AnnotationType)?) | ("STRING" Identifier ("," Identifier)* ("=" StringExpression)?) | (("INT" | "DOUBLE" | "FLOAT") Identifier ("," Identifier)* ("=" NumberExpression)?) | ("BOOLEAN" Identifier ("," Identifier)* ("=" BooleanExpression)?) | ("WORDLIST" Identifier ("=" WordListExpression)?) | ("WORDTABLE" Identifier ("=" WordTableExpression)?) | ("TYPELIST" Identifier ("=" TypeListExpression)?) | ("STRINGLIST" Identifier ("=" StringListExpression)?) | (("INTLIST" | "DOUBLELIST" | "FLOATLIST") Identifier ("=" NumberListExpression)?) | ("BOOLEANLIST" Identifier ("=" BooleanListExpression)?)) AnnotationType -> BasicAnnotationType | declaredAnnotationType BasicAnnotationType -> ('COLON'| 'SW' | 'MARKUP' | 'PERIOD' | 'CW'| 'NUM' | 'QUESTION' | 'SPECIAL' | 'CAP' | 'COMMA' | 'EXCLAMATION' | 'SEMICOLON' | 'NBSP'| 'AMP' | '_' | 'SENTENCEEND' | 'W' | 'PM' | 'ANY' | 'ALL' | 'SPACE' | 'BREAK') BlockDeclaration -> "BLOCK" "(" Identifier ")" RuleElementWithCA "{" Statements "}" AutomataDeclaration -> "RULES" "(" Identifier ")" RuleElementWithCA "{" Statements "}"
Syntax of statements and rule elements SimpleStatement -> RuleElements ";" RuleElements -> RuleElement+ RuleElement -> RuleElementType | RuleElementLiteral | RuleElementComposed | RuleElementDisjunctive RuleElementType -> TypeExpression QuantifierPart? ("{" Conditions? Actions? "}")? RuleElementWithCA -> TypeExpression QuantifierPart? "{" Conditions? Actions? "}" RuleElementLiteral -> SimpleStringExpression QuantifierPart? "{" Conditions? Actions? "}" RuleElementComposed -> "(" RuleElements ")" QuantifierPart? "{" Conditions? Actions? "}" RuleElementDisjunctive -> "(" (TypeExpression | SimpleStringExpression) ("|" (TypeExpression | SimpleStringExpression) )+ ")" QuantifierPart? "{" Conditions? Actions? }" QuantifierPart -> "*" | "*?" | "+" | "+?" | "?" | "??" | "[" NumberExpression "," NumberExpression "]" | "[" NumberExpression "," NumberExpression "]?" Conditions -> Condition ( "," Condition )* Actions -> "->" Action ( "," Action)*
